CREATE
EXTENSION IF NOT EXISTS pg_trgm;
CREATE INDEX translation_text_gin_trgm_index ON translation USING GIN (text gin_trgm_ops);

UPDATE user_account
SET account_type = 'LOCAL';
UPDATE user_account
SET account_type = 'THIRD_PARTY'
WHERE password IS NULL;
UPDATE user_account
SET account_type = 'LDAP'
WHERE password IS NULL
  AND third_party_auth_type IS NULL;

CREATE
EXTENSION IF NOT EXISTS unaccent;
CREATE
EXTENSION IF NOT EXISTS pg_trgm;

CREATE
OR REPLACE FUNCTION public.f_unaccent(text)
RETURNS text
LANGUAGE sql IMMUTABLE PARALLEL SAFE STRICT AS
$func$
SELECT public.unaccent('public.unaccent', $1)
           $func$;

CREATE INDEX translation_text_unaccent_gin_trgm_index ON translation USING GIN ( lower (f_unaccent(text)) gin_trgm_ops);
CREATE INDEX key_name_unaccent_gin_trgm_index ON key USING GIN (lower (f_unaccent(name)) gin_trgm_ops);
CREATE INDEX namespace_name_unaccent_gin_trgm_index ON namespace USING GIN (lower (f_unaccent(name)) gin_trgm_ops);

create unique index permission_null_idx on permission (organization_id);
insert into permission (id, type, organization_id, created_at, updated_at)
select nextval('permission_id_seq'), base_permissions, id, current_date, current_date
from organization;

UPDATE user_account
SET is_initial_user = true
WHERE id = (
    SELECT id FROM user_account WHERE username != '___implicit_user' ORDER BY id LIMIT 1
    );

UPDATE user_account
SET account_type = 'MANAGED'
WHERE account_type = 'LDAP';

ALTER TABLE BATCH_STEP_EXECUTION ADD CREATE_TIME TIMESTAMP NOT NULL DEFAULT '1970-01-01 00:00:00';

ALTER TABLE BATCH_STEP_EXECUTION ALTER COLUMN START_TIME DROP NOT NULL;

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS DROP COLUMN DATE_VAL;

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS DROP COLUMN LONG_VAL;

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS DROP COLUMN DOUBLE_VAL;

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS ALTER COLUMN TYPE_CD TYPE VARCHAR(100);

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS RENAME TYPE_CD TO PARAMETER_TYPE;

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS ALTER COLUMN KEY_NAME TYPE VARCHAR(100);

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS RENAME KEY_NAME TO PARAMETER_NAME;

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS ALTER COLUMN STRING_VAL TYPE VARCHAR(2500);

ALTER TABLE BATCH_JOB_EXECUTION_PARAMS RENAME STRING_VAL TO PARAMETER_VALUE;

CREATE OR REPLACE FUNCTION empty_json(j jsonb) RETURNS boolean AS '
BEGIN
RETURN j = ''{}''::jsonb;
END;
' LANGUAGE plpgsql IMMUTABLE;
