CREATE
EXTENSION IF NOT EXISTS pg_trgm;
CREATE INDEX translation_text_gin_trgm_index ON translation USING GIN (text gin_trgm_ops);

UPDATE user_account
SET account_type = 'LOCAL';
UPDATE user_account
SET account_type = 'THIRD_PARTY'
WHERE password IS NULL;
UPDATE user_account
SET account_type = 'LDAP'
WHERE password IS NULL
  AND third_party_auth_type IS NULL;

CREATE
EXTENSION IF NOT EXISTS unaccent;
CREATE
EXTENSION IF NOT EXISTS pg_trgm;

CREATE
OR REPLACE FUNCTION public.f_unaccent(text)
RETURNS text
LANGUAGE sql IMMUTABLE PARALLEL SAFE STRICT AS
$func$
SELECT public.unaccent('public.unaccent', $1)
$func$;

CREATE INDEX translation_text_unaccent_gin_trgm_index ON translation USING GIN ( lower(f_unaccent(text)) gin_trgm_ops);
CREATE INDEX key_name_unaccent_gin_trgm_index ON key USING GIN (lower(f_unaccent(name)) gin_trgm_ops);
CREATE INDEX namespace_name_unaccent_gin_trgm_index ON namespace USING GIN (lower(f_unaccent(name)) gin_trgm_ops);

create unique index permission_null_idx on permission(organization_id);
insert into permission (id, type, organization_id, created_at, updated_at)
select nextval('permission_id_seq'), base_permissions, id, current_date, current_date
from organization;

insert into api_key_scopes_enum (api_key_id, scopes_enum)
select api_key_id, 'KEYS_CREATE' from api_key_scopes_enum where scopes_enum = 'KEYS_EDIT';
insert into api_key_scopes_enum (api_key_id, scopes_enum)
select api_key_id, 'KEYS_DELETE' from api_key_scopes_enum where scopes_enum = 'KEYS_EDIT';
